import {withApollo} from '../apollo';
import { useQuery } from '@apollo/react-hooks';
import { GET_LAUNCHES } from '../graphql/get-launches';
import CardLaunch from '../components/CardLaunch';
import styled from 'styled-components';
import Masonry from 'react-masonry-css';

import { ThemeProvider } from '@zendeskgarden/react-theming';
import { Pagination } from '@zendeskgarden/react-pagination';



const LaunchesContainer = () => {
    const [initialState] = useState([{ currentPage: 1 }]);

    const {loading, error, data} = useQuery(GET_LAUNCHES, {
        variables: { limit: 36 }
    });

    if(loading) return <p>Loading...</p>
    if(error) return <p>Error...</p>
    else
    // console.log(data)
    return (
        <div className="container mx-auto px-10">
            <h1 className="text-white mb-10 text-lg">Past Launches</h1>
            <CardsContainer>
                <Masonry
                    breakpointCols={4}
                    className="my-masonry-grid"
                    columnClassName="my-masonry-grid_column">
                        <ThemeProvider>
                            {data.launchesPast.map(launch => <CardLaunch key={launch.mission_name} launch={launch} className="mr" />)}
                            <Pagination
                                totalPages={3}
                                currentPage={state.currentPage}
                                onChange={currentPage => setState({ currentPage })}
                            />

                        </ThemeProvider>
                </Masonry>
            </CardsContainer>
        </div>
    )
}

const CardsContainer = styled.div`
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    width: 80%;
    margin: 0 auto;
`;



export default withApollo({ ssr: true })(LaunchesContainer);
