import { useState } from 'react';
import createPagination from '../helpers/createPagination';

const Pagination = () => {
    const [currentPage, setCurrentPage] = useState(1);

    const { pagination } = createPagination({
        numberOfArticles: 36,
        articlesPerPage: 12,
        numberOfButtons: 4,
        currentPage
    })

    const handleClick = page => setCurrentPage(page);

    <div className="pagination">
        <ul>
            <li className={`${pagination[0] === currentPage && "disabled"}`} onClick={handleClick.bind(null, currentPage - 1)}>
                Prev
            </li>
            {pagination.map(page => (
                <li className={`${currentPage === page && "active"}`} onClick={handleClick.bind(null, page)} >
                    {page}
                </li>
            ))}
            <li className={`${pagination.reverse()[0] === currentPage && "disabled"}`} onClick={handleClick.bind(null, currentPage + 1)} >
                Next
            </li>
        </ul>
    </div>
}

export default Pagination;
