import Moment from "react-moment";
import "moment-timezone";
import classNames from 'classnames';
import { Container, Body, Title, Text, Image, Button } from '../styles/CardLaunch';
// Moment.globalTimezone = "America/Mexico_City";
// Card
const CardLaunch = ({ launch, classes, children, ...restProps }) => {
    const getImage = imgs => imgs[Math.floor(Math.random() * imgs.length)];
    return(
        <Container className={classNames('card', classes)} {...restProps}>
            <div key={launch.id}>

                {/* <img className="w-full" src={getImage(launch.links.flickr_images)} alt={launch.mission_name}/>  */}
                <CardLaunch.Image src={getImage(launch.links.flickr_images)} alt={launch.mission_name}/> 

                <div className="px-6 py-4">
                    <h1 className={"font-bold text-xl mb-2 " + (launch.launch_success ? 'success' : 'hidden')}>{launch.mission_name}</h1>
                    <p><span className="font-bold">Fecha:</span> <Moment format="D MMM YYYY" withTitle tz="America/Mexico_City">{launch.launch_date_utc}</Moment></p>
                    <p><span className="font-bold">Hora:</span> <Moment format="hh:mm a" tz="America/Mexico_City">{launch.launch_date_utc}</Moment></p>


                    <a href={launch.links.wikipedia} target="_blank" rel="noopener noreferrer" className="block mt-1 text-lg leading-tight font-semibold text-gray-900 hover:underline">More Info</a> 
                </div>

            </div>
        </Container>
    )
}

CardLaunch.Body = function CardBody({ classes, children, ...restProps}) {
    return (
        <Body className={classNames('card__body', classes)} {...restProps}>
            {children}
        </Body>
    )
}

CardLaunch.Title = function CardTitle({ classes, children, ...restProps}) {
    return (
        <Title className={classNames('card__title', classes)} {...restProps}>
            {children}
        </Title>
    )
}

CardLaunch.Text = function CardText({ classes, children, ...restProps}) {
    return (
        <Text className={classNames('card__text', classes)} {...restProps}>
            {children}
        </Text>
    )
}

CardLaunch.Image = function CardImage({ src, alt, classes, ...restProps}) {
    return (
        <Image 
        src={src}
        alt={alt}
        className={classNames('card__image'), classes}
        {...restProps}
        />
    )
}

CardLaunch.Button = function CardButton({ classes, children, ...restProps}) {
    return (
        <Button type="button" className={classNames('card__button', classes)} {...restProps}>
            {children}
        </Button>
    )
}

export default CardLaunch;
