import {withApollo} from '../apollo';
import { useQuery } from '@apollo/react-hooks';
import { GET_LAUNCHES } from '../graphql/get-launches';
import Layout from '../components/Layout';
import styled from 'styled-components';

const Title = styled.h1`
    font-size: 8rem;
    font-weight: bold;
    margin: 0 auto;
    color: white;
    z-index: 2;
    position: relative;
    padding: 0 8% 12rem;
    z-index: 3;
`;

const SubTitle = styled.h3`
    font-size: 3rem;
    font-weight: bold;
    margin: 0 auto;
    color: white;
    z-index: 2;
    position: relative;
    padding: 10rem 8% 0;
    z-index: 3;
`;

const Text = styled.h4`
    font-size: 2rem;
    font-weight: 400;
    text-align: right;
    line-height: 1.4em;
    margin: 0 0 0 auto;
    color: white;
    z-index: 2;
    position: relative;
    z-index: 3;
    /* width: 60vw; */
`;

const Header = styled.header`
    height: 60vh;
    width: 100%;
    padding-top: 100px;
    overflow: hidden;
    position: relative;
    &:before{
        content: '';
        width: 100%;
        height:100%;
        background: rgba(0,0,0,0.15);
        top: 0;
        left: 0;
        position: absolute;
        z-index: 1;
    }
`;

const Image = styled.img`
    height: 80vh;
    position: absolute;
    top: 0;
    left: 50%;
    transform: translateX(-50%);
    z-index: 0;
`;

const Container = styled.div`
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    width: 100%;
    margin: 0 auto;
    background: white;
    div{
        width: 50%;
        &:first-child{
            padding: 7vh 0;
        }
    }
    img{
        width: 100%;
    }
`;


const About = () => {
    const {loading, error, data} = useQuery(GET_LAUNCHES, {
        variables: { limit: 12 }
    });

    if(loading) return <p>Loading...</p>
    if(error) return <p>Error...</p>
    else
    console.log(data)
    return (
        <Layout>
            <Header>
                <SubTitle>About</SubTitle>
                <Title>{data.company.name}</Title>
                <Image src="/images/about.jpg" alt="Logo"/> 
            </Header>
            <Container>
                <div>
                    <Text>
                        {data.company.summary}
                    </Text>
                </div>
                <div>
                    <img src="/images/musk.jpg" alt="Logo"/> 
                </div>
            </Container>
        </Layout>

    )
}

// export default About;
export default withApollo({ ssr: true })(About);
