import{ useState } from 'react';
import {withApollo} from '../apollo';
import { useQuery } from '@apollo/react-hooks';
import { GET_LAUNCHES } from '../graphql/get-launches';
import CardLaunch from '../components/CardLaunch';
import styled from 'styled-components';
import Masonry from 'react-masonry-css';

// import Pagination from '../components/Pagination';
import { ThemeProvider } from '@zendeskgarden/react-theming';
import { Pagination } from '@zendeskgarden/react-pagination';



const LaunchesContainer = () => {
    const [currentPage, setCurrentPage] = useState(1);
    const [postsPerPage] = useState(12);


    const {loading, error, data} = useQuery(GET_LAUNCHES, {
        variables: { limit: 36 }
    });

    if(loading) return <p>Loading...</p>
    if(error) return <p>Error...</p>
    else
    console.log(data)

    //Get current post
    const indexOfLastPost = currentPage * postsPerPage;
    const indexOfFirstPost = indexOfLastPost - postsPerPage;
    const currentPosts = data.launchesPast.slice(indexOfFirstPost, indexOfLastPost);

    const paginate = pageNumber => setCurrentPage(pageNumber)

    return (
        <div className="container mx-auto px-10">
            <Title className="">Past Launches</Title>
            <CardsContainer>
                <Masonry
                    breakpointCols={4}
                    className="my-masonry-grid"
                    columnClassName="my-masonry-grid_column">
                        {currentPosts.map(launch => <CardLaunch key={launch.mission_name} launch={launch} className="mr" loading={loading}/>)}

                </Masonry>
                {/* <Pagination postsPerPage={postsPerPage} totalPosts={data.launchesPast.length} paginate={paginate}/> */}
                <ThemeProvider>
                    <Pagination
                        totalPages={postsPerPage}
                        currentPage={currentPosts}
                        onChange={currentPosts => setState({ currentPosts })}
                    />
                </ThemeProvider>

            </CardsContainer>
        </div>
    )
}

const CardsContainer = styled.div`
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    margin: 0 auto;
`;
const Title = styled.h1`
    font-size: 3rem;
    font-weight: bold;
    margin: 0 25px;
    color: white;
`;


export default withApollo({ ssr: true })(LaunchesContainer);
