import styled from 'styled-components';

export const Container = styled.div`
    display: flex;
    flex-direction: column;
    min-width: 0;
    /* width: 20%; */
    background-color: white;
    border: 1px solid rgba(0,0,0,0.125);
    border-radius: 0.15rem;
    overflow: hidden;
    position: relative;
    &:hover{
        .card__more{
            opacity: 1;
            div{
                opacity: 1;
                transform: scale(1);
            }
        }
    }

`;

export const Body = styled.div`
    display: flex;
    flex-direction: column;
    flex: 1 1 auto;
    min-height: 1px;
    padding: 1.25rem;
    position: absolute;
    bottom: 0;
    width: 100%;
    background: rgb(2,0,36);
    background: linear-gradient(0deg, rgba(2,0,36,1) 0%, rgba(255,255,255,0) 66%);
`;

export const Title = styled.h1`
    font-size: 1.25rem;
    font-weight: bold;
    margin: 0 0 5px;
`;

export const Text = styled.p`
    line-height: 20px;
    color: #fff;
    font-size: .9rem;
`;

export const Image = styled.img`
    width: 100%;
`;

export const LinkTo = styled.a`
    cursor: pointer;
    margin: 0 auto;
    width: 50px;
    height: 50px;
    border-radius: 50%;
    color: #fff;
    background-color: #28a745;
    display: block;
    font-weight: bold;
    text-align: center;
    user-select: none;
    padding: 0.375rem 0.75rem;
    font-size: 1rem;
    line-height: 40px;
    transition: all 0.15s ease-in-out;
    z-index: 9;
    &:hover {
        color: #fff;
        background-color: #218838;
        border-color: #1e7e34;
    }
`;

export const More = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    width: 100%;
    height: 100%;
    position: absolute;
    background-color: rgba(0,0,0,.7);
    opacity: 0;

    transition: all 0.1s ease-in-out;
    overflow: hidden;
    div{
        transition: all 0.1s ease-in-out;
        transform: scale(.3);
        transition-delay: .1s;
    }
    .card__title{
        color: #fff;
        text-align: center;
    }
`;
