import styled from 'styled-components';

export const Container = styled.div`
    display: flex;
    flex-direction: column;
    min-width: 0;
    /* width: 20%; */
    background-color: white;
    border: 1px solid rgba(0,0,0,0.125);
    border-radius: 0.15rem;
    overflow: hidden;
    position: relative;
`;

export const Body = styled.div`
    display: flex;
    flex-direction: column;
    flex: 1 1 auto;
    min-height: 1px;
    padding: 1.25rem;
    position: absolute;
    bottom: 0;
    width: 100%;
    background: rgb(2,0,36);
    background: linear-gradient(0deg, rgba(2,0,36,1) 0%, rgba(255,255,255,0) 36%);
`;

export const Title = styled.h1`
    font-size: 1.25rem;
    font-weight: bold;
    margin: 0;
`;

export const Text = styled.p`
    line-height: 20px;
    color: #fff;
    font-size: 1rem;
`;

export const Image = styled.img`
    width: 100%;
`;

export const LinkTo = styled.a`
    cursor: pointer;
    margin-top: auto;
    width: 50px;
    height: 50px;
    border-radius: 50%;
    color: #fff;
    background-color: #28a745;
    display: block;
    font-weight: bold;
    text-align: center;
    user-select: none;
    padding: 0.375rem 0.75rem;
    font-size: 1rem;
    line-height: 40px;
    transition: color 0.15s ease-in-out, background-color 0.15s ease-in-out;
    position: absolute;
    transform: translate(-50%, -50%);
    top: 50%;
    left: 50%;
    &:hover {
        color: #fff;
        background-color: #218838;
        border-color: #1e7e34;
    }
`;
