import { useQuery, useState } from '@apollo/react-hooks';
import { GET_LAUNCHES } from '../graphql/get-launches';
import { LaunchPast } from '../components/LaunchPast';
import createPagination from '../helpers/createPagination';

export function LaunchesContainer() {
    const {loading, error, data} = useQuery(GET_LAUNCHES, {
        variables: { limit: 36 }
    });

    const [currentPage, setCurrentPage] = useState(1);

    const { pagination } = createPagination({
        numberOfArticles: 36,
        articlesPerPage: 12,
        numberOfButtons: 4,
        currentPage
    })

    if(loading) return <p>Loading...</p>
    if(error) return <p>Error...</p>
    else
    // console.log(data)
    return (
        <div className="container mx-auto px-10">
            <div className="grid grid-cols-1 sm:grid-cols-3 lg:grid-cols-4 gap-4">
                {data.launchesPast.map(launch => <LaunchPast key={launch.mission_name} launch={launch} />)}
            </div>
        </div>
    )

}
