import ApolloClient from 'apollo-boost';

const client = ({initialState}) => {
    new ApolloClient({
        uri: 'https://api.spacex.land/graphql/',
        cache: new InMemoryCache().restore(initialState || {}) // hydrate cache
    });
}

export default client;



